    private void tuCBMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tuCBMouseClicked
        // TODO add your handling code here:
         String hakakses=null;
        if(tuCB.isSelected()){
            hakakses="TU";
        }else if(keuanganCB.isSelected()){
            hakakses="KEUANGAN";
             }else if(kepsekCB.isSelected()){
            hakakses="KEPALA SEKOLAH";
        }
        String u_nama = null;
        if(usernameTF.getText().equals("") || passwordTF.getText().equals("")|| hakakses.equals("")){
            JOptionPane.showMessageDialog(null, "Lengkapi inputan login","Pesan",JOptionPane.WARNING_MESSAGE);
        } else {
            try {
                setKoneksi();
                String sql="select * from pengguna where username='"+usernameTF.getText()
                +"' and password='"+passwordTF.getText()+"'and hakakses='"+hakakses+"'";
                ResultSet rs=st.executeQuery(sql);

                if (rs.next()) {
                    u_nama = rs.getString("nama");
                }
                rs.last();

                if(rs.getRow()>=1){
                    
login.setU_nama(u_nama);
                    if(hakakses.equals("TU")){
                        menuutama MU=new menuutama();
                        MU.setLocationRelativeTo(null);
                        MU.setVisible(true);
                        MU.hakAksesTu();
                        
                        dispose();
                        JOptionPane.showMessageDialog(null,"Behasil Login","SEKOLAH CITA-CITA", JOptionPane.WARNING_MESSAGE);
                    }else if(hakakses.equals("KEUANGAN")){
                        menuutama MU=new menuutama();
                        MU.setLocationRelativeTo(null);
                        MU.setVisible(true);
                        MU.hakAksesKeuangan();
                       
                        dispose();
                  JOptionPane.showMessageDialog(null,"Berhasil Login","SEKOLAH CITA-CITA", JOptionPane.WARNING_MESSAGE);
                  }else if(hakakses.equals("KEPALA SEKOLAH")){
                        menuutama MU=new menuutama();
                        MU.setLocationRelativeTo(null);
                        MU.setVisible(true);
                        MU.hakAksesKepsek();
                    }
                }else if(rs.getRow()==0){
                    JOptionPane.showMessageDialog(null,"Username, password atau hak akses tidak sesuai","SEKOLAH CITA-CITA", JOptionPane.WARNING_MESSAGE);
                    usernameTF.grabFocus();
                }
            } catch (Exception e) {
            }
        }
    }//GEN-LAST:event_tuCBMouseClicked

